(()=>{"use strict";const t=document.querySelector(".post_list");fetch("http://localhost:3000/posts").then((t=>t.json())).then((s=>function(s){let n="";s.forEach((t=>{const s=`\n        <div class="post_list post_block">\n          <div class="post-image">\n            <img src="https://picsum.photos/300/200?random=${t.id}">\n          </div>\n          <div class="post-text">\n            <h3>${t.title}</h3>\n            <p>${t.text}</p>\n          </div>\n        </div>\n        `;n+=s})),t.innerHTML=n}(s)))})();